mapper.select:
SELECT tu.* , @rownum := @rownum +1 AS row_num FROM (SELECT t.task_id AS taskId, t.task_name AS taskName, t.task_status_code AS taskStatusCode, t.task_contract_address AS taskContractAddress, t.task_purpose AS taskPurpose, t.task_framework AS taskFramework, t.task_data_type AS taskDataType, t.task_max_trainer AS taskMaxTrainer, t.task_port AS taskPort, u.user_id AS organizationUserId, u.user_name AS userName
,tp.data_path AS dataPath ,tp.user_id AS trainerUserId
FROM TASK t, USER u
,TASK_PARTICIPATE tp
WHERE t.user_id = u.user_id
AND u.user_id = #{organizationUserId}
AND t.task_status_code = #{taskStatusCode}
AND tp.task_Id = t.task_Id
) tu, (SELECT @rownum:=0) r WHERE 1=1
AND lower(tu.taskName) like lower(CONCAT('%', #{searchText}, '%')) OR lower(tu.userName) like lower(CONCAT('%', #{searchText}, '%'))
AND tu.taskId=#{taskId}
ORDER BY taskId DESC
